import React, { useState } from 'react';

// initialize state with bio and state with the social medias
// toggle those states between true or false to toggle and display them
// toggle the states accordingly if its true make it false to hide it
// display with 2 buttons that control the 2 toggles

const Profile = () => {
  const [showBio, setShowBio] = useState(true);
  const [showSocialMedia, setShowSocialMedia] = useState(true);

  const handleToggle = (section) => {
    if (section === 'socialMedia') {
      setShowBio(!showBio);
    } else if (section === 'bio') {
      setShowSocialMedia(!showSocialMedia);
    }
  };

  return (
    <div>
      <h2>Profile</h2>
      <div>
        <button onClick={() => handleToggle('socialMedia')}>Toggle</button>
        {showBio && (
          <div>
            <h3>Bio:</h3>
            <p>Write your bio here...</p>
          </div>
        )}
      </div>

      <div>
        <button onClick={() => handleToggle('bio')}>
          {showSocialMedia ? 'Hide Social Media' : 'Show Social Media'}
        </button>
        {showSocialMedia && (
          <div>
            <h3>Social Media Links:</h3>
            <ul>
              <li>Facebook</li>
              <li>Twitter</li>
              <li>Instagram</li>
            </ul>
          </div>
        )}
      </div>    
    </div>
  );
};

export default Profile;
